    if(!preg_match(&amp;quot;/\\\\|\/|\~|\`|\!|\@|\#|\%|\^|\*|\-|\+|\=|\&amp;#123;|\&amp;#125;|\&amp;quot;|\&#39;|\,|\.|\;|\?|flag|GLOBALS|echo|var_dump|print/i&amp;quot;, $c)&amp;amp;&amp;amp;$c&amp;lt;=16)&amp;#123;
         eval(&amp;quot;$c&amp;quot;.&amp;quot;;&amp;quot;);
